import React, { useState } from "react";
import styled, { keyframes } from "styled-components";

// Dummy data
const dummyData = [
  { name: "John Doe", score: 85, details: "CEO of XYZ Corp. Known for innovation and leadership." },
  { name: "Jane Smith", score: 92, details: "Renowned lawyer with a strong track record in corporate law." },
  { name: "Emily Davis", score: 78, details: "Tech entrepreneur with several successful startups." },
];

const App = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [results, setResults] = useState([]);

  const handleSearch = () => {
    const filteredData = dummyData.filter((person) =>
      person.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setResults(filteredData);
  };

  return (
    <Wrapper>
      <Navbar>
        <NavLogo>PersonScore</NavLogo>
        <NavItems>
          <NavItem>Home</NavItem>
          <NavItem>About</NavItem>
          <NavItem>Contact</NavItem>
        </NavItems>
      </Navbar>

      <MainSection>
        <SearchContainer>
          <SearchTitle>Discover a Person's Score</SearchTitle>
          <SearchInput
            type="text"
            placeholder="Enter a name..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <SearchButton onClick={handleSearch}>Search</SearchButton>
        </SearchContainer>

        <ResultsContainer>
          {results.length === 0 ? (
            <NoResults>No results found. Try searching for someone!</NoResults>
          ) : (
            results.map((person, index) => (
              <ResultCard key={index}>
                <CircularScore score={person.score}>
                  <ScoreText>{person.score}</ScoreText>
                </CircularScore>
                <ResultContent>
                  <ResultName>{person.name}</ResultName>
                  <ResultDetails>{person.details}</ResultDetails>
                </ResultContent>
              </ResultCard>
            ))
          )}
        </ResultsContainer>
      </MainSection>

      <Footer>
        <FooterText>Â© 2025 PersonScore. All rights reserved.</FooterText>
      </Footer>
    </Wrapper>
  );
};

export default App;
